import { styled } from 'styled-components';

// -------------- <Widget /> -----------------------

export const Widget = styled.div`

    background-color: #FFFFFF;
    color: #000000;
    border-bottom: 3px solid #ccab28;
    background-color: #f0f0f0;

    .buttons {
        padding-top: 0;
    }

    @media (max-width: 768px) {

        .buttons {
            position: absolute;
            top: 0;
            left: 0;
        }

        align-items: flex-start;
    }

    .selected img {
        border: 2px solid rgb(102, 175, 233) !important;
    }
`;


// -------------- <LocalizationBar /> -----------------------

export const LocalizationBar = styled.ul`

    position: absolute;
    list-style-type: none;
    display: flex;
    align-items: center;
    top: 6px;
    left: 15px;
    margin: 0;
    padding: 0;

    li {
        height: 24px;
        display: flex;
        align-items: center;

        a {
            img {
                display: block;
            }
        }
    }

    li:not(:last-child) {
        margin-right: 0.5em;
    }
`;

// -------------- <Navigation /> ----------------------------

export const Navigation = styled.nav`

    display: flex;
    justify-content: center;
    height: 100%;

    a {
        color: #000000;
    }

    .lindat-common {
        font-family: Droid Sans, Helvetica, Arial, sans-serif;
        box-sizing: border-box;
        font-size: 14px
    }

    .lindat-common *, .lindat-common :after, .lindat-common :before {
        box-sizing: inherit
    }

    @media (max-width: 768px) {
        margin-top: 2em;
        margin-right: 0;
        justify-content: flex-start;
        flex-direction: column;
    }

    @media (min-width: 768px) {
        .lindat-logo {
            background:url('data:image/png;base64,') no-repeat 50%;
            min-width: 100px
        }

        .lindat-logo > span {
            display:none
        }

        .clarin-logo {
            background:url('data:image/png;base64,') no-repeat 50%;
            min-width: 100px
        }

        .clarin-logo > span {
            display: none;
        }
    }
`;

// ----------------- <Menu /> ---------------------------------

export const Menu = styled.ul`

    margin: 0;
    padding: 0;
    display: none;
    flex-direction: row;
    flex-wrap: nowrap;
    justify-content: center;
    align-items: stretch;
    list-style-type: none;

    &.lindat-open {
        display: flex;
        flex-direction: column;
    }

    > li {
        display: block;
    }

    > li > a {
        height: 100%;
        text-decoration: none;
        padding: 10px 15px;
        font-size: 16px;
        display: flex;
        align-items: center;
        color: #000;
    }

    > li > a:hover {
        outline: 0;
    }

    @media (min-width: 768px) {
        margin-top: 0;
        height: 53px;
        display: flex;

        > li {
            border-right: 1px dotted #e4e4e4;
        }

        > li > a {
            display: flex;
            align-items: center;
            font-size: 14px;
            padding: 0 15px;
            font-weight: bolder;
            text-align: center;
            border-bottom: 3px solid transparent;
        }

        > li > a:hover {
            outline: 0;
            border-bottom-color: #ccab28;
        }
    }

    @media (max-width: 768px) {
        > li > a:hover {
            background-color: #d7d7d7;
        }
    }

`;

// --------------------- <MenuButton /> --------------------------

export const MenuButton = styled.button`

    width: 2.5em;
    height: 2.5em;
    margin: 5px 14px;
    background-color: transparent;
    border: 1px solid #ddd;
    border-radius: 4px;
    cursor: pointer;
    outline: none;

    .lindat-icon-bar+.lindat-icon-bar {
        margin-top: 4px
    }

    .lindat-icon-bar {
        display: block;
        width: 22px;
        height: 2px;
        border-radius: 1px;
        background-color: #ccc
    }

    @media (min-width: 768px) {
        display: none;
    }
`;